import { Component, Input } from '@angular/core';

@Component({
  selector: 'app-acknowledge',
  templateUrl: './acknowledge.component.html',
  styleUrls: ['./acknowledge.component.css']
})
export class AcknowledgeComponent {
  @Input() name: string;
  @Input() mobile: string;
  @Input() email: string;
}
<div class="row">
    <div class="col-xs-12 pt-10">
        <header class="h3 text-center">Thank you For Your Registration</header>
    </div>
</div>

<p>
    Thank you for registering your child {{ name }}. 
    We have send additional details to your email address {{ email }}.
    We will contact you on your mobile at {{ mobile }} to confirm the demo class.
</p>
