import { Component, Input } from '@angular/core';

@Component({
  selector: 'app-acknowledge',
  templateUrl: './acknowledge.component.html',
  styleUrls: ['./acknowledge.component.css']
})
export class AcknowledgeComponent {
  @Input() name: string;
  @Input() mobile: string;
  @Input() email: string;
}
<div class="row">
    <div class="col-xs-12 pt-5">
        <div class="text-center">
            <img src="assets/email_confirmation.jpg" alt="Description of image">
        </div>
        <div>
            <header class="h3 text-center pt-4 pb-2">Thank You For Your Registration</header>
            <p class="text-muted text-center">
                We have send you a confirmation email with additional details to your email id {{ email }}.
            </p>
            <!-- <p class="text-muted text-center">
                We will contact you on your mobile at {{ mobile }} to confirm the demo class.
            </p> -->
        </div>
    </div>
</div>
